ValidationRule.errorConditionFormula:
OR ( AND( OR( ISPICKVAL( loan__Draw_Billing_Method__c , 'Sliding Billing'), ISPICKVAL( loan__Repayment_Billing_Method__c , 'Sliding Billing')),  loan__Sliding_Billing_Setup__c == '' ),

 AND( loan__Sliding_Billing_Setup__c != '',  AND( NOT( ISPICKVAL( loan__Draw_Billing_Method__c , 'Sliding Billing') ),  NOT( ISPICKVAL( loan__Repayment_Billing_Method__c , 'Sliding Billing') ) ) )
)
